<script>
	import { onDestroy, onMount } from 'svelte';
	import ColorPicker, { ChromeVariant } from 'svelte-awesome-color-picker';
	import { domToBlob, domToPng } from 'modern-screenshot';

	let firstColor = $state('');
	let secondColor = $state('');
	let waitingGeneration = $state(false);
	let shiftPressed = $state(false);

	onMount(async () => {
		firstColor = generateHexaColor();
		secondColor = generateHexaColor();

		document.addEventListener('keydown', (e) => {
			if (e.key === 'Shift') {
				shiftPressed = true;
			} else if (e.key === ' ') {
				e.preventDefault();
				firstColor = generateHexaColor();
				secondColor = generateHexaColor();
			}
		});

		document.addEventListener('keyup', (e) => {
			if (e.key === 'Shift') {
				shiftPressed = false;
			}
		});
	});

	const generateHexaColor = () => {
		const randomColor = Math.floor(Math.random() * 16777215).toString(16);
		const paddedColor = randomColor.padStart(6, '0');
		return `#${paddedColor}`;
	};

	const downloadPNG = async (e) => {
		waitingGeneration = true;

		/**
		 * @type {HTMLElement | any}
		 */
		const node = document.getElementById('logo');
		const dataUrl = await domToPng(node, {
			scale: 1,
			quality: 1
		});

		if (shiftPressed) {
			try {
				await navigator.clipboard.write([
					new ClipboardItem({
						'image/png': domToBlob(node, {
							scale: 1,
							quality: 1
						})
					})
				]);
			} catch (err) {
				alert(
					"Erreur lors de la copie dans le presse-papiers. Veuillez essayer de télécharger l'image."
				);
			}
		} else {
			const a = document.createElement('a');
			a.href = dataUrl;
			a.download = `bouns-logo-${firstColor}-${secondColor}.png`;
			a.click();
		}

		waitingGeneration = false;
	};

	const downloadSVG = async (e) => {
		waitingGeneration = true;

		const svg = document.querySelector('svg');
		const serializer = new XMLSerializer();
		const svgString = serializer.serializeToString(svg);

		if (shiftPressed) {
			try {
				//to clipboaord in string
				await navigator.clipboard.write([
					new ClipboardItem({
						'text/plain': svgString
					})
				]);
			} catch (err) {
				alert(
					"Erreur lors de la copie dans le presse-papiers. Veuillez essayer de télécharger l'image."
				);
			}
		} else {
			const blob = new Blob([svgString], { type: 'image/svg+xml' });
			const url = URL.createObjectURL(blob);
			const link = document.createElement('a');
			link.href = url;
			link.download = 'pattern.svg';
			document.body.appendChild(link);
			link.click();
			document.body.removeChild(link);
		}

		waitingGeneration = false;
	};
</script>

